    FileInputStream inputStream = <span style="color:#8b008b;font-weight:bold">null</span>;
    FileOutputStream outputStream = <span style="color:#8b008b;font-weight:bold">null</span>;
    <span style="color:#8b008b;font-weight:bold">try</span> {
