柚子茶室 https://yoziming.github.io/post/210815-agg-go-al-%E4%B8%80%E6%AC%A1%E6%80%A7%E9%9A%8A%E5%88%97/ -<h1 id="一次性隊列">一次性隊列</h1>
<pre tabindex="0"><code>//用結構體管理隊列
type Queue struct {
	maxSize int
	array   [3]int
	front   int //隊列首(不包含)
	rear    int //隊尾(含)
}

//添加數據val到隊列
func (q *Queue) AddQueue(val int) (err error) {
	//先判斷是否已滿
	if q.rear == q.maxSize-1 {
		return errors.New(&quot;queue full&quot;)
	}
	q.rear++
	q.array[q.rear] = val
	return
}

//取出數據
func (q *Queue) GetQueue() (val int, err error) {
	//先判斷是否為空
	if q.rear == q.front {
		return -1, errors.New(&quot;queue empty&quot;)
	}
	q.front++
	val = q.array[q.front]
	return val, err

}

//顯示隊列
func (q *Queue) ShowQueue() {
	fmt.Println(&quot;當前隊列=&quot;)
	for i := q.front + 1; i &lt;= q.rear; i++ {
		fmt.Printf(&quot;array[%d]=%d\t&quot;, i, q.array[i])
	}
	fmt.Println(&quot;隊列顯示完畢&quot;)
}

func main() {
	//創建一個隊列
	queue1 := Queue{
		maxSize: 3,
		front:   -1,
		rear:    -1,
	}
	var key string
	var val int
	for {
		fmt.Println(&quot;\n等待操作中...&quot;)
		fmt.Println(&quot;輸入add 添加&quot;)
		fmt.Println(&quot;輸入get 取出&quot;)
		fmt.Println(&quot;輸入show 顯示&quot;)
		fmt.Println(&quot;輸入exit 離開&quot;)

		fmt.Scanln(&amp;key)
		switch key {
		case &quot;add&quot;:
			fmt.Println(&quot;輸入要加入的數值...&quot;)
			fmt.Scanln(&amp;val)
			err := queue1.AddQueue(val)
			if err != nil {
				fmt.Println(err.Error())
			} else {
				fmt.Println(&quot;加入成功&quot;)
			}
		case &quot;get&quot;:
			val, err := queue1.GetQueue()
			if err != nil {
				fmt.Println(err.Error())
			} else {
				fmt.Println(&quot;取出成功&quot;, val)
			}

		case &quot;show&quot;:
			queue1.ShowQueue()
		case &quot;exit&quot;:
			os.Exit(0)
		}

	}
}

</code></pre>- https://yoziming.github.io/post/210815-agg-go-al-%E4%B8%80%E6%AC%A1%E6%80%A7%E9%9A%8A%E5%88%97/ - yoziming

柚子茶室 https://yoziming.github.io/post/210815-agg-go-al-%E4%BA%8C%E5%88%86%E6%A8%B9%E7%9A%843%E7%A8%AE%E9%81%8D%E6%AD%B7/ -<h1 id="二分樹的3種遍歷">二分樹的3種遍歷</h1>
<pre tabindex="0"><code>package main

import &quot;fmt&quot;

type Hero struct {
	No    int
	Name  string
	Left  *Hero
	Right *Hero
}

//前序遍歷 先輸出root 再輸出左子樹 再輸出右子樹，會中左右一層層往下
func PreOrder(node *Hero) {
	if node != nil {
		fmt.Printf(&quot;no=%d name=%s\n&quot;, node.No, node.Name)
		PreOrder(node.Left)
		PreOrder(node.Right)
	}
}

//中序遍歷 左子樹 root 右子樹
func InfixOrder(node *Hero) {
	if node != nil {
		InfixOrder(node.Left)
		fmt.Printf(&quot;no=%d name=%s\n&quot;, node.No, node.Name)
		InfixOrder(node.Right)
	}
}

//後序遍歷 左子樹  右子樹 root，會一路到最左最下開始打
func PostOrder(node *Hero) {
	if node != nil {
		PostOrder(node.Left)
		PostOrder(node.Right)
		fmt.Printf(&quot;no=%d name=%s\n&quot;, node.No, node.Name)

	}
}

func main() {
	//建一個二分樹
	root := &amp;Hero{
		No:   1,
		Name: &quot;宋江&quot;,
	}

	left1 := &amp;Hero{
		No:   2,
		Name: &quot;無用&quot;,
	}

	right1 := &amp;Hero{
		No:   3,
		Name: &quot;你軌&quot;,
	}
	root.Left = left1
	root.Right = right1
	right2 := &amp;Hero{
		No:   4,
		Name: &quot;林沖&quot;,
	}
	right1.Right = right2
	node21 := &amp;Hero{
		No:   21,
		Name: &quot;無用-下左&quot;,
	}
	node22 := &amp;Hero{
		No:   22,
		Name: &quot;無用-下右&quot;,
	}
	left1.Left = node21
	left1.Right = node22

	PreOrder(root)
	fmt.Println(&quot;==========&quot;)
	InfixOrder(root)
	fmt.Println(&quot;==========&quot;)
	PostOrder(root)

}

