柚子茶室 https://yoziming.github.io/post/210815-agg-go-al-%E8%BF%B7%E5%AE%AE%E9%81%9E%E8%BF%B4/ -<h1 id="迷宮遞迴">迷宮遞迴</h1>
<pre tabindex="0"><code>func SetWay(myMap *[8][7]int, a int, b int) bool {
	//a,b 表示對地圖的那個點測試

	//找到出口
	if myMap[6][5] == 2 {
		return true
	} else {
		//繼續找
		if myMap[a][b] == 0 { //可探測的點
			myMap[a][b] = 2            //踩上去了，再探上下左右，但是我知道要往下往右探
			if SetWay(myMap, a+1, b) { //下
				return true
			} else if SetWay(myMap, a, b+1) { //右
				return true
			} else if SetWay(myMap, a-1, b) { //上
				return true
			} else if SetWay(myMap, a, b-1) { //左
				return true
			} else { //死路
				myMap[a][b] = 3
				return false
			}
		} else { //不可探測的點
			return false
		}
	}
}
func main() {
	//定義地圖 1=牆壁 0=沒走過的點 2=通路 3=走過的死路
	var myMap [8][7]int

	//四周設置圍牆
	for i := 0; i &lt; 7; i++ {
		myMap[0][i] = 1
		myMap[7][i] = 1
	}
	for i := 0; i &lt; 8; i++ {
		myMap[i][0] = 1
		myMap[i][6] = 1
	}
	myMap[3][1] = 1
	myMap[3][2] = 1

	//看樣子
	for i := 0; i &lt; 8; i++ {
		for j := 0; j &lt; 7; j++ {
			fmt.Print(myMap[i][j], &quot; &quot;)
		}
		fmt.Println()
	}

	SetWay(&amp;myMap, 1, 1)
	fmt.Println(&quot;已探完&quot;)

	for i := 0; i &lt; 8; i++ {
		for j := 0; j &lt; 7; j++ {
			fmt.Print(myMap[i][j], &quot; &quot;)
		}
		fmt.Println()
	}
}

</code></pre>- https://yoziming.github.io/post/210815-agg-go-al-%E8%BF%B7%E5%AE%AE%E9%81%9E%E8%BF%B4/ - yoziming

柚子茶室 https://yoziming.github.io/post/210815-agg-go-al-%E9%9B%99%E5%90%91%E9%8F%88%E8%A1%A8/ -<h1 id="雙向鏈表">雙向鏈表</h1>
<pre tabindex="0"><code>type HeroNode struct {
	no   int
	name string
	pre  *HeroNode //表示指向前一個節點
	next *HeroNode //表示指向下一個節點
}

//給鏈表插入節點
func Insert(head *HeroNode, newHeroNode *HeroNode) {
	//先找到最後的節點，創建一個輔助
	temp := head
	for {
		if temp.next == nil { //表示找到最後
			break
		}
		temp = temp.next //不斷往下找
	}
	//把newHeroNode加到屁股
	temp.next = newHeroNode
	newHeroNode.pre = temp
}

//根據No從小到大插入
func InsertNo(head *HeroNode, newHeroNode *HeroNode) {
	//想找到適當的節點，創建一個輔助
	temp := head
	for {
		if temp.next == nil { //表示找到最後
			break
		} else if temp.next.no &gt; newHeroNode.no {
			//表示new要插到temp後
			break
		} else if temp.next.no == newHeroNode.no {
			//表示new這個編號已存在
			fmt.Println(newHeroNode.no, &quot;該編號已存在&quot;)
			return
		}
		temp = temp.next //不斷往下找

	}
	//把newHeroNode加到找到的地方
	newHeroNode.next = temp.next
	newHeroNode.pre = temp
	if temp.next != nil {
		temp.next.pre = newHeroNode //temp的下一個的前一個=新
	}
	temp.next = newHeroNode
}

//根據no刪除一個節點
func DelHeroNode(head *HeroNode, id int) {
	//想找到適當的節點，創建一個輔助
	temp := head
	for {
		if temp.next == nil { //表示找到最後
			break
		} else if temp.next.no == id {
			//表示找到了
			fmt.Println(id, &quot;該編號已刪除&quot;)
			temp.next = temp.next.next
			if temp.next != nil {
				temp.next.pre = temp
			}
			return
		}
		temp = temp.next //不斷往下找
	}

}

//顯示
func List(head *HeroNode) {
	//創建一個輔助
	temp := head
	if temp.next == nil { //空鏈表?
		fmt.Println(&quot;空&quot;)
		return
	}
	for {
		fmt.Printf(&quot;[%d,%s]==&gt;&quot;, temp.next.no, temp.next.name)
		temp = temp.next      //不斷往下找
		if temp.next == nil { //到尾了
			fmt.Println(&quot;完畢&quot;)
			break
		}
	}
}

func main() {
	//創建一個頭節點(空的)
	head := &amp;HeroNode{}
	hero1 := &amp;HeroNode{
		no:   1,
		name: &quot;宋江&quot;,
	}
	hero2 := &amp;HeroNode{
		no:   2,
		name: &quot;李奎&quot;,
	}
	hero3 := &amp;HeroNode{
		no:   3,
		name: &quot;李軌&quot;,
	}
	InsertNo(head, hero3)
	InsertNo(head, hero1)
	InsertNo(head, hero2)

	List(head)
	DelHeroNode(head, 3)
	List(head)
}

</code></pre>- https://yoziming.github.io/post/210815-agg-go-al-%E9%9B%99%E5%90%91%E9%8F%88%E8%A1%A8/ - yoziming

柚子茶室 https://yoziming.github.io/post/210810-agg-go-channel/ -<h1 id="channel通道">channel通道</h1>
<h2 id="lock-sync與chan基本認識">lock sync與chan基本認識</h2>
<pre tabindex="0"><code>//階乘1-n 把各個數放到map中 用goroutine完成
var (
	myMap = make(map[int]int, 10)
	//聲明一個全局的互斥鎖
	lock sync.Mutex
	//來點管道 記得要make初始化才能用
	intChan chan int = make(chan int, 3)

	//聲明類型為空接口可以接收任意類型
	allChan chan interface{} = make(chan interface{}, 5)
	//注意後面沒聲明容量則不給用
)

type Cat struct {
	Name string
	Age  int
}

func test(n int) {
	res := 1
	for i := 1; i &lt;= n; i++ {
		res = res * i
	}
	//把res放進mayMap
	//加鎖
	lock.Lock()
	myMap[n] = res
	//解鎖
	lock.Unlock()
}

func main() {
	//啟用多個協程
	for i := 1; i &lt;= 1; i++ {
		go test(i)
	}
	//如果不加鎖concurrent map iteration and map write
	lock.Lock()
	for i, v := range myMap {
		fmt.Printf(&quot;map[%d]=%v\n&quot;, i, v)
	}
	lock.Unlock()

	//看看管道
	fmt.Println(intChan)
	//出來是一個地址，表示誰要經過這 (當然本身也有一個地址但不重要)
	fmt.Printf(&quot;長度=%v 容量=%v\n&quot;, len(intChan), cap(intChan))

	//向管道寫入數據
	intChan &lt;- 10
	fmt.Printf(&quot;長度=%v 容量=%v\n&quot;, len(intChan), cap(intChan))
	num1 := 5
	intChan &lt;- num1
	intChan &lt;- 1
	fmt.Printf(&quot;長度=%v 容量=%v\n&quot;, len(intChan), cap(intChan))
	//intChan &lt;- 12 //超過3個太長報錯

	//從管道取數據
	var num2 int = &lt;-intChan
	fmt.Println(num2)
	fmt.Printf(&quot;長度=%v 容量=%v\n&quot;, len(intChan), cap(intChan))
	//先進先出，取完超過一樣報錯
	num3 := &lt;-intChan
	fmt.Println(num3)
	fmt.Printf(&quot;長度=%v 容量=%v\n&quot;, len(intChan), cap(intChan))

	//關於allChan
	allChan &lt;- 10
	allChan &lt;- &quot;ABC&quot;
	cat1 := Cat{&quot;小花&quot;, 3}
	allChan &lt;- cat1
	//可以亂塞一通

	//想拿到第三個元素 先把兩個推出來
	&lt;-allChan
	&lt;-allChan

	newCat := &lt;-allChan //從管道中取出來的cat1
	fmt.Printf(&quot;類型=%T 值=%v\n&quot;, newCat, newCat)
	// fmt.Printf(&quot;%v\n&quot;, newCat.Name) //雖然類型一樣但這樣用不通過
	//必須類型斷言
	a := newCat.(Cat)
	fmt.Printf(&quot;%v\n&quot;, a.Name)

}
