<pre tabindex="0"><code>type Monster struct {
	Name string  `json:&quot;name&quot;` //反射機制
	Age  int     `json:&quot;age&quot;`
	Sal  float64 `json:&quot;sal&quot;`
}

func test1() {
	monster1 := Monster{
		Name: &quot;牛魔王&quot;,
		Age:  50,
		Sal:  8000.0,
	}
	data, err := json.Marshal(&amp;monster1)
	if err != nil {
		fmt.Println(err)
