<pre tabindex="0"><code>func reflectTest01(b interface{}) {
	//通過反射獲取傳入的各種訊息
	//先拿reflect.Type
	rType := reflect.TypeOf(b)
	fmt.Println(&quot;rType=&quot;, rType)

	rVal := reflect.ValueOf(b)
	fmt.Println(&quot;rVal=&quot;, rVal)
	// n1:=10+rVal //rVal他看起來是int但其實是type reflect.Value
	n1 := 10 + rVal.Int() //用.Int()轉換後就可以了
	// n3 := rVal.Float()//像這樣也不行的，一定要確認類型
	fmt.Println(&quot;n1=&quot;, n1)
	// fmt.Println(&quot;n3=&quot;, n3)

	//把rVal轉回interface{}
	iV := rVal.Interface()
	n2 := iV.(int)
	fmt.Println(&quot;n2=&quot;, n2)
}

func reflectTest02(b interface{}) {
	//通過反射獲取傳入的各種訊息
	//先拿reflect.Type
	rType := reflect.TypeOf(b)
	fmt.Println(&quot;rType=&quot;, rType)

	rVal := reflect.ValueOf(b)
	fmt.Println(&quot;rVal=&quot;, rVal)

	//把rVal轉回interface{}
	iV := rVal.Interface()
	//斷言回來
	n2, ok := iV.(Student)
	if !ok {
		fmt.Println(&quot;從interface{}轉換回Student失敗&quot;)
	}
	fmt.Println(&quot;n2=&quot;, n2)
	fmt.Println(&quot;n2.Name=&quot;, n2.Name)

	//獲取變量對應的kind，可以從Type也能從val拿。
	//比如這個type是包名.Student，kind是stuct，可以說定義上kind包含type
	fmt.Println(&quot;kind=&quot;, rType.Kind())
	fmt.Println(&quot;kind=&quot;, rVal.Kind())

}

type Student struct {
	Name string
	Age  int
}

func main() {
	var num int = 100
	reflectTest01(num)
	fmt.Println(&quot;===============================&quot;)
	stu := Student{
		Name: &quot;tom&quot;,
		Age:  20,
	}
	reflectTest02(stu)

}
