func main01() {
	// 1.用RPC連接服務器
	// conn, err := rpc.Dial(&quot;tcp&quot;, &quot;127.0.0.1:8899&quot;)
	conn, err := jsonrpc.Dial(&quot;tcp&quot;, &quot;127.0.0.1:8899&quot;)
