</span></span><span style="display:flex;"><span>        System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;用戶&#34;</span> + b1.<span style="color:#658b00">getCustomer</span>(0).<span style="color:#658b00">getName</span>() +
</span></span><span style="display:flex;"><span>                <span style="color:#cd5555">&#34;的餘額為&#34;</span> + balance);
