</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(detail);
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;明細結束!&#34;</span>);
</span></span><span style="display:flex;"><span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#34;2&#34;</span>:
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;===2.登記收入===&#34;</span>);
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;輸入金額...&#34;</span>);
</span></span><span style="display:flex;"><span>                    String moneyInStr = scan.<span style="color:#658b00">next</span>();
</span></span><span style="display:flex;"><span>                    <span style="color:#00688b;font-weight:bold">int</span> moneyIn = Integer.<span style="color:#658b00">parseInt</span>(moneyInStr);
</span></span><span style="display:flex;"><span>                    money += moneyIn;
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;輸入說明...&#34;</span>);
</span></span><span style="display:flex;"><span>                    String infoIn = scan.<span style="color:#658b00">next</span>();
</span></span><span style="display:flex;"><span>                    detail += <span style="color:#cd5555">&#34;\n收入\t&#34;</span> + moneyInStr + <span style="color:#cd5555">&#34;\t\t&#34;</span> + money + <span style="color:#cd5555">&#34;\t&#34;</span> + infoIn;
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;登記成功!&#34;</span>);
</span></span><span style="display:flex;"><span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#34;3&#34;</span>:
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;===3.登記支出===&#34;</span>);
