</span></span><span style="display:flex;"><span>                    String moneyOutStr = scan.<span style="color:#658b00">next</span>();
</span></span><span style="display:flex;"><span>                    <span style="color:#00688b;font-weight:bold">int</span> moneyOut = Integer.<span style="color:#658b00">parseInt</span>(moneyOutStr);
</span></span><span style="display:flex;"><span>                    money -= moneyOut;
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;輸入說明...&#34;</span>);
</span></span><span style="display:flex;"><span>                    String infoOut = scan.<span style="color:#658b00">next</span>();
</span></span><span style="display:flex;"><span>                    detail += <span style="color:#cd5555">&#34;\n支出\t&#34;</span> + moneyOutStr + <span style="color:#cd5555">&#34;\t\t&#34;</span> + money + <span style="color:#cd5555">&#34;\t&#34;</span> + infoOut;
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;登記成功!&#34;</span>);
</span></span><span style="display:flex;"><span>                    <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                <span style="color:#8b008b;font-weight:bold">case</span> <span style="color:#cd5555">&#34;4&#34;</span>:
</span></span><span style="display:flex;"><span>                    System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;確認退出?&lt;Y/N&gt;不分大小寫...&#34;</span>);
</span></span><span style="display:flex;"><span>                    String confirmExit = scan.<span style="color:#658b00">next</span>(); <span style="color:#228b22">// 獲取使用者輸入
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>                    <span style="color:#8b008b;font-weight:bold">if</span> (Objects.<span style="color:#658b00">equals</span>(confirmExit, <span style="color:#cd5555">&#34;y&#34;</span>) || Objects.<span style="color:#658b00">equals</span>(confirmExit, <span style="color:#cd5555">&#34;Y&#34;</span>)) {
</span></span><span style="display:flex;"><span>                        flag = <span style="color:#8b008b;font-weight:bold">false</span>;
</span></span><span style="display:flex;"><span>                        System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;歡迎下次使用!&#34;</span>);
</span></span><span style="display:flex;"><span>                        <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                    } <span style="color:#8b008b;font-weight:bold">else</span> <span style="color:#8b008b;font-weight:bold">if</span> (Objects.<span style="color:#658b00">equals</span>(confirmExit, <span style="color:#cd5555">&#34;n&#34;</span>) || Objects.<span style="color:#658b00">equals</span>(confirmExit, <span style="color:#cd5555">&#34;N&#34;</span>)) {
</span></span><span style="display:flex;"><span>                        System.<span style="color:#658b00">out</span>.<span style="color:#658b00">println</span>(<span style="color:#cd5555">&#34;繼續使用&#34;</span>);
</span></span><span style="display:flex;"><span>                        <span style="color:#8b008b;font-weight:bold">break</span>;
</span></span><span style="display:flex;"><span>                    }
