柚子茶室 https://yoziming.github.io/post/210810-agg-go-inherit/ -<h1 id="繼承">繼承</h1>
<pre tabindex="0"><code>type Student struct {
	Name  string
	Age   int
	Score int
	//共有的字段
}

type Pupil struct {
	Student //套入
	Covid19 bool
}

type College struct {
	Student //套入指針效率更好
	SexExp  bool
}

func (stu *Student) ShowInfo() {
	fmt.Printf(&#34;姓名=%v 年齡=%v 成績=%v\n&#34;, stu.Name, stu.Age, stu.Score)
}

func (stu *Student) SetScore(s int) {
	stu.Score = s
}

func (p *Pupil) Test() {
	fmt.Println(&#34;小學生考試&#34;)
}

func main() {
	pupilA := &amp;Pupil{}
	pupilA.Student.Name = &#34;tome&#34;
	pupilA.Student.Age = 8
	pupilA.Covid19 = true

	pupilA.Test()
	pupilA.Student.SetScore(66)
	pupilA.Student.ShowInfo()
	fmt.Println(&#34;是否得肺炎&#34;, pupilA.Covid19)

	C1 := College{}
	C1.Student.Name = &#34;John&#34;
	C1.Age = 23 //其實可以省略.Student
	C1.SexExp = false

	C1.Student.SetScore(39)
	C1.ShowInfo() //其實可以省略.Student
	fmt.Println(&#34;是否破處&#34;, C1.SexExp)

	//如果有低能把結構體跟匿名結構體用了相同的字段或方法，會採取就近訪問原則
	// 如果在一個結構中嵌入多個匿名結構，且匿名結構有同名的字段，那就必須指明匿名結構否則報錯

	//組合
	var d D
	d.a.Name = &#34;xxxx&#34; //有名字的結構體訪問時就要上完整名字
}

//組合
type D struct {
	a Student //有名字的結構體
}
</code></pre>- https://yoziming.github.io/post/210810-agg-go-inherit/ - Yoziming
