<pre tabindex="0"><code>package main

import (
	&quot;fmt&quot;
	&quot;io/ioutil&quot;
	&quot;reflect&quot;
	&quot;strings&quot;
)

type RedisConfig struct {
	Host     string `ini:&quot;host&quot;`
	Port     int    `ini:&quot;port&quot;`
	Password string `ini:&quot;password&quot;`
	Database int    `ini:&quot;database&quot;`
}
type MysqlConfig struct {
	Address  string `ini:&quot;address&quot;`
	Port     int    `ini:&quot;port&quot;`
	Password string `ini:&quot;password&quot;`
	Username string `ini:&quot;username&quot;`
}

type Config struct {
	MysqlConfig `ini:&quot;mysql&quot;`
	RedisConfig `ini:&quot;redis&quot;`
}

func loadIni(fileName string, data interface{}) (err error) {
	// 0. 參數校驗
	// 0.1 傳來的data必須是結構體類型指針
	t := reflect.TypeOf(data)
	// fmt.Println(t.Kind())
	if t.Kind() != reflect.Ptr {
		err = fmt.Errorf(&quot;傳來的data必須是指針&quot;)
		return
	}
	if t.Elem().Kind() != reflect.Struct {
		err = fmt.Errorf(&quot;傳來的data必須是結構體類型指針&quot;)
		return
	}
	// 1. 讀取文件~&gt;Byte類型
	b, err := ioutil.ReadFile(fileName)
	if err != nil {
		return
	}
	//轉成字符串
	lineSlice := strings.Split(string(b), &quot;\r\n&quot;)
	// fmt.Println(lineSlice)
	// 2. 一行一行讀取數據
	var structName string
	for idx, line := range lineSlice {
		line = strings.TrimSpace(line) //去除多餘空格
		// 2.1 如果是註釋就跳過
		if strings.HasPrefix(line, &quot;;&quot;) || strings.HasPrefix(line, &quot;#&quot;) {
			continue
		}
		//純空行就跳過
		if len(line) == 0 {
			continue
		}
		// 2.2 如果是[]開頭表示是節
		if strings.HasPrefix(line, &quot;[&quot;) {
			if line[0] != '[' || line[len(line)-1] != ']' {
				err = fmt.Errorf(&quot;line:%d syntax error&quot;, idx+1)
				return
			}
			//去掉這行的首尾跟空格拿到內容
			section := strings.TrimSpace(line[1 : len(line)-1])
			if len(section) == 0 {
				err = fmt.Errorf(&quot;line:%d syntax error&quot;, idx+1)
				return
			}
			//根據section去data反射對應的結構體
			for i := 0; i &lt; t.Elem().NumField(); i++ {
				field := t.Elem().Field(i)
				if section == field.Tag.Get(&quot;ini&quot;) {
					//說明找到了，把字段名記下
					structName = field.Name
					fmt.Println(section, structName)
				}

			}

		} else {
			// 2.3 剩下的就是分割的鍵值對
			//先排除異常的行
			if strings.Index(line, &quot;=&quot;) == -1 || strings.HasPrefix(line, &quot;=&quot;) {
				err = fmt.Errorf(&quot;line:%d syntax error&quot;, idx+1)
				return
			}
			index := strings.Index(line, &quot;=&quot;)
			key := strings.TrimSpace(line[:index])
			// value := strings.TrimSpace(line[:index+1])
			v := reflect.ValueOf(data)
			sValue := v.Elem().FieldByName(structName) //拿到嵌套結構體的值信息
			sType := sValue.Type()                     //拿到嵌套結構體的類型信息
			if sType.Kind() != reflect.Struct {
				err = fmt.Errorf(&quot;%s應該為一個結構體&quot;, structName)
				return
			}
			var fieldName string
			//遍歷嵌套結構體每一個字段 判斷tag是否=key
			for i := 0; i &lt; sValue.NumField(); i++ {
				field := sType.Field(i)
				if field.Tag.Get(&quot;ini&quot;) == key {
					fieldName = field.Name
					break
				}
			}
			//根據fieldName取出字段賦值
			fileObj := sValue.FieldByName(fieldName)
			fmt.Println(fieldName, fileObj)
		}

	}
	return
}

func main() {
	var cfg Config
	err := loadIni(&quot;C:/config.ini&quot;, &amp;cfg)
	if err != nil {
		panic(err)
	}
}

