<blockquote>
<p>自己寫用來一次取代多個名詞，去除多餘空格或符號等等</p>
</blockquote>
<pre tabindex="0"><code>package main

import (
	&quot;bufio&quot;
	&quot;fmt&quot;
	&quot;io&quot;
	&quot;os&quot;
	&quot;strings&quot;
)

var data []string = make([]string, 128)

func main() {
	readByBufio()
	for _, v := range data {
		if v == &quot;&quot; {
			break
		}
		fmt.Print(v)
	}

}
func readByBufio() {
	file, err := os.Open(&quot;C:/test.txt&quot;)
	if err != nil {
		fmt.Printf(&quot;os.Open err %s\n&quot;, err)
		return
	}
	defer file.Close()
	reader := bufio.NewReader(file)
	for i := 0; ; i++ {
		line, err := reader.ReadString('\n')
		if err == io.EOF {
			return
		}
		if err != nil {
			fmt.Printf(&quot;reader.ReadString err %s\n&quot;, err)
			return
		}
		line = strings.ReplaceAll(line, &quot;++&quot;, &quot;+&quot;) //去除多餘++
		line = strings.ReplaceAll(line, &quot;  &quot;, &quot; &quot;) //去除多餘空格
		line = strings.ReplaceAll(line, &quot;*&quot;, &quot;&quot;)   //去除多餘*
		line = strings.ReplaceAll(line, &quot;AAAA&quot;, &quot;BBBB&quot;)
		line = strings.ReplaceAll(line, &quot;&quot;, &quot;&quot;)
		data[i] = line
	}

}
