func (mm *MyMoney) exit() {
	fmt.Println(&quot;確定使否退出?(Y/N)&quot;)
	choice := &quot;&quot;
	for {
		fmt.Scanln(&amp;choice)
		if choice == &quot;Y&quot; || choice == &quot;N&quot; {
			break
		}
		fmt.Println(&quot;錯誤，確定使否退出?(Y/N)&quot;)
	}
	if choice == &quot;Y&quot; {
		fmt.Println(&quot;\n-----------------結束-----------------&quot;)
		mm.loop = false
	} else {
		mm.loop = true
	}
}

