var arr1 = [...]int{5, 9, 6, 8, 7, 3, 4, 2, 1, 0}

func bubble(x []int) {
	for j := 1; j &lt; len(x); j++ {
		for i := 0; i &lt; len(x)-1; i++ {
			if x[i] &gt; x[i+1] {
				x[i], x[i+1] = x[i+1], x[i]
			}
		}
	}
}
func bi(x int, s []int, left int, right int) {
	middle := (right + left) / 2
	if left &gt; right {
		fmt.Println(&quot;找不到&quot;)
		return
	}
	if x &lt; s[middle] {
		//在左邊
		bi(x, s, left, middle-1)
	} else if x &gt; s[middle] {
		//在右邊
		bi(x, s, middle+1, right)
	} else {
		fmt.Println(&quot;找到下標=&quot;, middle)
	}

}
func main() {
	s1 := arr1[:]
	bubble(s1)
	fmt.Println(s1)
	bi(9, s1, 0, len(s1)-1)
}

