	monkey1.Flying()

}
</code></pre><h1 id="接口實踐排序">接口實踐排序</h1>
<pre tabindex="0"><code>type Hero struct {
	Name string
	Age  int
}

//聲明一個Hero結構體的切片類型
type HeroSlice []Hero

//實現接口
func (hs HeroSlice) Len() int {
	return len(hs)
}

func (hs HeroSlice) Less(i, j int) bool {
	//return hs[i].Age &lt; hs[j].Age //Less就是決定使用哪個字段來排序
	return hs[i].Name &lt; hs[j].Name //Less就是決定使用哪個字段來排序
}

func (hs HeroSlice) Swap(i, j int) {
	hs[i], hs[j] = hs[j], hs[i]
}

func main() {
	//定義一個數組跟切片
	var intSlice = []int{5, 3, 1, 2, 4}
	//來排序
	sort.Ints(intSlice)
	fmt.Println(intSlice)

	//系統提供的方法

	//創建切片內容
	var heroes HeroSlice
	for i := 0; i &lt; 10; i++ {
		hero := Hero{
			Name: fmt.Sprintf(&quot;英雄%d&quot;, rand.Intn(100)),
			Age:  rand.Intn(100),
		}
		//放進切片 隨機創建了十個hero放進heroes這個切片
		heroes = append(heroes, hero)
	}

	//排序前
	for _, v := range heroes {
		fmt.Println(v)
	}

	//調用sort.Sort
	sort.Sort(heroes)
	//因為我的結構體能實現接口，就可以享受這些方便
	fmt.Println(&quot;---排序後---&quot;)
	for _, v := range heroes {
		fmt.Println(v)
	}
}
</code></pre><h1 id="類型斷言">類型斷言</h1>
<pre tabindex="0"><code>func TypeJudge(items ...interface{}) {
	for index, x := range items {
		switch x.(type) {
		case bool:
			fmt.Printf(&quot;第%v個參數是bool類型,值是%v\n&quot;, index+1, x)
		case int, int32, int64:
			fmt.Printf(&quot;第%v個參數是int類型,值是%v\n&quot;, index+1, x)
		case string:
			fmt.Printf(&quot;第%v個參數是string類型,值是%v\n&quot;, index+1, x)
		default:
			fmt.Printf(&quot;第%v個參數是不明類型,值是%v\n&quot;, index+1, x)
		}
	}
}

func main() {
	var n1 float64 = 2.3
	var n2 int = 30
	var name string = &quot;tom&quot;
	n4 := 300

	TypeJudge(n1, n2, name, n4)
}

