		if input == names[i] {
			index = i
			break
		}
	}
	if index != -1 {
		fmt.Printf(&quot;找到%v,下標=%v \n&quot;, input, index)
	} else {
		fmt.Println(&quot;比對失敗...&quot;)
	}
}
